import React, { useState } from 'react';
import { View, Text, Image, Linking } from 'react-native';
import { RectButton } from 'react-native-gesture-handler';
import AsyncStorage from '@react-native-community/async-storage'

import heartOutlineIcon from '../../assets/images/icons/heart-outline.png'
import unFavoriteIcon from '../../assets/images/icons/unfavorite.png'
import whatsappIcon from '../../assets/images/icons/whatsapp.png'

import styles from './styles'
import api from '../../server/api';


export interface Teacher{
    id:number;
    avatar:string;
    bio:string;
    cost:number;
    name: string;
    subject:string;
    whatsapp:string;
}

interface TeacherItemProps {
    teacher: Teacher;
    favorited: boolean;
}


const TeacherItem: React.FC<TeacherItemProps> = ({ teacher, favorited }) => {

    const [isFavorited, setIsFavorited] = useState(favorited)

    function handleLinkWhatsapp() {

        api.post('connections',{
            user_id:teacher.id
        })

        Linking.openURL(`whatsapp://send?text=Olá, tenho interesse na sua aula de ${teacher.subject}!&phone=${teacher.whatsapp}`)
    }

    async function handleToggleFavorite() {

        const favorites = await AsyncStorage.getItem('favorites');
            
        let favoritesArray =[];

        if (favorites){
            favoritesArray = JSON.parse(favorites);
        }


        if(isFavorited){
            //remover dos favoritos
            const favoriteIndex= favoritesArray.findIndex((teacherItem:Teacher) => {
                return teacherItem.id === teacher.id
            });
            favoritesArray.splice(favoriteIndex, 1);

            setIsFavorited(false)

        }else{
            //adc nos favoritos

            // const favorites = await AsyncStorage.getItem('favorites');
            
            // let favoritesArray =[];

            // if (favorites){
            //     favoritesArray = JSON.parse(favorites);
            // }
            favoritesArray.push(teacher);
        
            setIsFavorited(true);
        }

        await AsyncStorage.setItem('favorites', JSON.stringify(favoritesArray))
        
    }


    return(
        <View style={styles.container} >

            <View style={styles.profile}>
                <Image
                 source={{uri: teacher.avatar}}
                 style={styles.avatar}
                />

                    <View style={styles.profileInfo}>
                        <Text style={styles.name}> {teacher.name} </Text>
                        <Text style={styles.subject}>{teacher.subject}</Text>
                    </View>
                    </View>
                <Text style={styles.bio}>
                {teacher.bio}
                </Text>

            
            <View style={styles.footer}>
                <Text style={styles.price}>
                    Preço/hora: {'   '} 
                    <Text style={styles.cost}>R$ {teacher.cost},00</Text>
                </Text>

                <View style={styles.buttonsContainer}>
                    
                        <RectButton onPress={handleToggleFavorite}  style={[
                            styles.favoriteButton, 
                            isFavorited ? styles.favorited : {},
                            ]} 
                        >

                            { isFavorited 
                                ?  <Image source={unFavoriteIcon} />
                                    
                                : <Image source={heartOutlineIcon} />
                            }
                          
                        </RectButton>

                        <RectButton onPress={handleLinkWhatsapp} style={styles.contactButton}>
                            <Image source={whatsappIcon} />
                            <Text style={styles.contactBtText}>Entre em Contato</Text>
                        </RectButton>
                </View>
            </View>
        </View>
    )
    
}

export default TeacherItem;
